mapper.select:
SELECT en_user.id,en_user.user_name userName, en_user.login_name loginName, '' reUserRoleId, en_user.user_status userStatus, en_user.remark, date_format(en_user.add_time, '%Y-%m-%d %H:%i:%s') addTime, date_format(en_user.update_time, '%Y-%m-%d %H:%i:%s') updateTime from sys_en_user en_user
and en_user.user_name like #{userNameCon}
and en_user.login_name like #{loginNameCon}
and exists (select 1 from sys_re_user_role re_ur where en_user.id = re_ur.fk_user_id and re_ur.fk_role_id in
#{item}
)
and en_user.user_status = #{userStatus}
order by en_user.login_name asc
